catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading || newValue == '') {
      return;
   }

   //Type appropriate comment here, and begin script below
   var ga = new GlideAjax('CatalogUserScripts');
	ga.addParam('sysparm_name', 'getUserExtendedInfo');
	ga.addParam('sysparm_userid', g_form.getValue('requested_for'));
	ga.getXML(doExtendedParse);
}

function doExtendedParse(response){
	var answer = response.responseXML.documentElement.getAttribute('answer');
	var obj = JSON.parse(answer);

	g_form.setValue("fdns_ds_title", obj.title);
	g_form.setValue("fdns_ds_organization", obj.department);
	g_form.setValue("fdns_ds_location", obj.location);
	g_form.setValue("fdns_ds_network_id", obj.user_name);
}
