catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading || newValue == '') {
      return;
   }

   //Type appropriate comment here, and begin script below
   var item = $("current_item");
	var guide = $("sysparm_guide");
	// if we are on the initial order guide page - allow the following code to run.
	if (item != null && guide != null && item.value == guide.value){

		g_form.hideErrorBox(control);
		if (newValue == '') {
			g_form.setValue('contract_supervisor_email', '');
			g_form.setValue('contract_supervisor_title', '');
			g_form.setValue('contract_supervisor_phone', '');
			return;
		}
		var nv = newValue + '';  //convert to string for compare
		var or = g_form.getValue('requested_for')  +'';  //convert to string for compare
		var sf = g_form.getValue('fed_supervisor') +''; //convert to string for compare

		if (nv == or){ // if contract supervisor (nv) and Requested For (or) match, reset and show error.
			g_form.setValue('contract_supervisor_email', '');
			g_form.setValue('contract_supervisor_title', '');
			g_form.setValue('contract_supervisor_phone', '');
			g_form.setValue('contract_supervisor', '');
			g_form.showErrorBox(control, "Requested For and 'Contract Supervisor or COR' can't be the same person.");
		}
		if (nv == sf) { //if contract supervisor (nv) and federal supervisor (sf) match, reset and show error.
			g_form.setValue('contract_supervisor_email', '');
			g_form.setValue('contract_supervisor_title', '');
			g_form.setValue('contract_supervisor_phone', '');
			g_form.setValue('contract_supervisor', '');
			g_form.showErrorBox(control, "'Contract Supervisor or COR' and 'Federal Supervisor / Manager' can't be the same person.");	
		}
		else{  // contract supervisor is not the same as the Requested For - proceed
			var ga = new GlideAjax('CatalogUserScripts');
		ga.addParam('sysparm_name', 'getUserExtendedInfo');
		ga.addParam('sysparm_userid', g_form.getValue('contract_supervisor'));
		ga.getXML(doExtendedParse);
		}
	}
	else{ 
		// code to run when on the item pages (choose options)
	}
}

function doExtendedParse(response){
	var answer = response.responseXML.documentElement.getAttribute('answer');
	var obj = JSON.parse(answer);

	g_form.setValue("contract_supervisor_title", obj.title);
	g_form.setValue("contract_supervisor_phone", obj.phone);
	g_form.setValue("contract_supervisor_email", obj.email);
}
