catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
   if (newValue == '') {
      return;
   }
	g_form.setValue('onboarding_action',''); 
	g_form.setValue('onboarding_eod','');
	g_form.setValue('onboarding_foreign','');
	g_form.setValue('onboarding_ses','No');
	g_form.setValue('onboarding_contract_name','');
	g_form.setValue('supervisor_fed','');
	g_form.setValue('supervisor_contract','');
	g_form.setValue('resources','');
	g_form.setValue('onboarding_emp_type','');


	var ga = new GlideAjax('CatalogUserScripts');
	ga.addParam('sysparm_name', 'getUserExtendedInfo');
	ga.addParam('sysparm_userid', g_form.getValue('onboarding_requestee'));
	ga.getXML(doExtendedParse);

}

	function doExtendedParse(response){
		var answer = response.responseXML.documentElement.getAttribute('answer');
		var obj = JSON.parse(answer);

		g_form.setValue("onboarding_email", obj.email);
		g_form.setValue("onboarding_phone", obj.phone);
		g_form.setValue("onboarding_lname", obj.last);
		g_form.setValue("onboarding_mname", obj.middle);
		g_form.setValue("onboarding_fname", obj.first);
		g_form.setValue("onboarding_room", obj.cube);
		g_form.setValue("onboarding_location", obj.location);		
	}
