sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// Add your code here

	var role = new GlideRecord('sys_user_role');
	role.addQuery('name', 'u_knowledge_recert');
	role.query();
	if(role.next()){
		var userRole = new GlideRecord('sys_user_has_role');
		userRole.addQuery('role', role.sys_id);
		userRole.addQuery('user', current.u_user);
		userRole.query();
		if(userRole.next()){
			//user already has the role, we don't need to add
		}
		else{
			// user does not have the role, we need to add
			var gr = new GlideRecord('sys_user_has_role');
			gr.initialize();
			gr.user = current.u_user;
			gr.role = role.sys_id;
			gr.insert();
		}
		var userRole1 = new GlideRecord('sys_user_has_role');
		userRole1.addQuery('role', role.sys_id);
		userRole1.addQuery('user', current.u_supervisor);
		userRole1.query();
		if(userRole1.next()){
			//supervisor already has the role, we don't need to add
		}
		else{
			// supervisor does not have the role, we need to add
			var gr1 = new GlideRecord('sys_user_has_role');
			gr1.initialize();
			gr1.user = current.u_supervisor;
			gr1.role = role.sys_id;
			gr1.insert();
		}
	}
})(current, previous);
