function getFiedValueInServerSide(){

	var gr = new GlideRecord('x_58872_needit_needit'); //Indicate the table to query from
	//The 'addQuery' line allows you to restrict the query to the field/value pairs specified (optional)
	//gr.addQuery('active', true);
	gr.query(); //Execute the query
	while (gr.next()) { //While the recordset contains records, iterate through them
		//Do something with the records returned
		if(gr.u_request_type != 'facilities'){

			//Use gr.fieldName to get the dropbox selection information
			//Use gr.getDisplayValue to get the field's value
			g_form.addInfoMessage('DATA :  ' + gr.u_request_type + " <> " +
			'Full Name : ' +gr.getDisplayValue('u_requested_for')+ " <> " +
			'Email : ' +gr.getDisplayValue('u_requested_for_email'));//+ " <> " +
			//'User ID : '+gs.getUserID());
			//gs.addInfoMessage('Full Name: ' +gr.getDisplayValue('u_requested_for'));

		}
	}
}
