function onChange(control, oldValue, newValue, isLoading, isTemplate) {

	var execute = true;
	if (isLoading && (g_form.getValue('assignment_group') == '9afaae376f489200a0b002e6eb3ee4c9' || (g_form.getValue('assignment_group') == '51d63d996f0ca200a0b002e6eb3ee436'))){
		execute = false;
	}

	if (execute){
		var caller = g_form.getReference('u_affected_user', setLocation);
	}
   }
	/*else {
   var u_opened_on_behalf_of = g_form.getReference('u_opened_on_behalf_of', setLocationOnBehalf);

	}
	*/


function setLocation(caller) {
   if (caller)
       g_form.setValue('location', caller.location);
	   g_form.setValue('u_suite', caller.u_suite);	   
	   var currentCube = g_form.getValue('u_cube_number');
	   if ((currentCube == '')||(currentCube == null)) {
	    g_form.setValue('u_cube_number', caller.u_cube_number);
	   }		   
	   var sitecode = g_form.getReference('location', setSiteCode);
}

function setSiteCode(sitecode) {
   if (sitecode)
	   g_form.setValue('u_site_code', sitecode.u_site_code);
}
