function onLoad() {
	//Type appropriate comment here, and begin script below
	var parse_number = /^-?\d+(?:\.\d*)?(?:e[+\-]?\d+)?$/i;
	var test = function (num) {return parse_number.test(num);};

	var phone_format = function (stringphonenumber) {
		var xyz = "";
		for(var i = 0; i < stringphonenumber.length; i++){
			xyz = xyz + stringphonenumber.charAt(i);
			if(i==2 || i==5){
				xyz = xyz+ "-"; 
			}
		}
		return xyz;   
	};

	var curr = "123*123*1234";//newValue+"";
	var phone = "";
	for(var j = 0; j < curr.length; j++){
		if(test(curr.charAt(j)+"") === true || test(curr.charAt(j)+"") === 'true')
			phone = phone + curr.charAt(j)+"";
	}
	g_form.setValue('home_phone', phone);
	g_form.addErrorMessage(phone_format(phone)+"");

	//g_form.addErrorMessage(phone_format(phone)+"");
