sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   //Type appropriate comment here, and begin script below
   var ga = new GlideAjax('CatalogUserScripts');
	ga.addParam('sysparm_name', 'getUserExtendedInfo');
	ga.addParam('sysparm_userid', g_form.getValue('requested_for'));
	ga.getXML(doExtendedParse);
}
function doExtendedParse(response){
	var answer = response.responseXML.documentElement.getAttribute('answer');
	var obj = JSON.parse(answer);

	//g_form.setValue("contact_email", obj.email);
	//g_form.setValue("time_zone", obj.time_zone);
	//	g_form.setValue("requested_for_title", obj.title);
	//	g_form.setValue("requested_for_cube", obj.cube);
	//	g_form.setValue("requested_for_location", obj.location);
	g_form.setValue("windows_user_login_id", obj.user_name);
	//	g_form.setValue("requested_for_site_code", obj.location);
	//g_form.setValue("agent_name", obj.full_name);

}
