sys_script_client.script:
function onLoad() {
   //Type appropriate comment here, and begin script below

	var email = "7031114589";
	var formettadEmail = "";
	for(var i = 0; i < email.length; i++){
		 formettadEmail = formettadEmail + email.charAt(i);
		if(i==2 || i==5){
			formettadEmail = formettadEmail+ "-"; 
		}
	}

	g_form.addErrorMessage("Email : " +formettadEmail);

}


function formettedEmail(stringEmail) {
	var formettadEmail = "";
	for(var i = 0; i < stringEmail.length; i++){
		formettadEmail = formettadEmail + stringEmail.charAt(i);
		if(i==2 || i==5){
			formettadEmail = formettadEmail+"-"; 
		}
	}
	return formettadEmail;	 
}
