function onLoad() {

	//alert("Thank you for loading the NeedIt form.   When you close this alert you will //be able to interact with the form.");

	//g_form.addInfoMessage("Loading ...");

	var gr = new GlideRecord('x_58872_needit_needit');
	gr.addQuery('u_requested_for', 'Abraham Lincoln');
	gr.query(myCallbackFunction); //Execute the query with callback function


	//After the server returns the query recordset, continue here
	function myCallbackFunction(gr){
			g_form.addInfoMessage("Exiting ...");

		//while (gr.next()) { //While the recordset contains records, iterate through them
			g_form.addInfoMessage("USER NAME : "+gr.u_requested_for);
		//}
	}

	//g_form.addInfoMessage("Exiting ...");
}










function findAllRecords(){
	var gr = new GlideRecord('x_58872_needit_needit'); //Indicate the table to query from
	//The 'addQuery' line allows you to restrict the query to the field/value pairs specified (optional)
	gr.addQuery('active', true);

	gr.query(); //Execute the query
	while (gr.next()) { //While the recordset contains records, iterate through them
		//Do something with the records returned
		//if(gr.state != 1){
			g_form.addInfoMessage('Names : ' + gr.u_requested_for);
			//}
		}
	}
	function TestAjaxCall(){
		//Type appropriate comment here, and begin script below
		var ga = new GlideAjax('MyAjaxCallScriptInclude');
		ga.addParam('sysparm_name','helloWorld');
		//ga.addParam('sysparm_user_name','Bob');
		ga.addParam('sysparm_userID', g_form.getValue('u_requested_for'));

		ga.getXML(HelloWorldParse);

		function HelloWorldParse(response) {
			var answer = response.responseXML.documentElement.getAttribute("answer");
			//alert(answer);
			g_form.addInfoMessage("FC-3 : " + answer);//Top-green

		}
	}
