sys_script_client.script:
/*
Sets the Phone Number to the affected user's phone number unless the phone number already has a value
*/
function onLoad() {
	//Type appropriate comment here, and begin script below
	if(g_form.getValue('u_phone_number') == ''){
		//alert('onLoad script - phone number was blank');
		var ga = new GlideAjax('CatalogUserScripts');
		ga.addParam('sysparm_name', 'getUserExtendedInfo');
		ga.addParam('sysparm_userid', g_form.getValue('u_affected_user'));
		ga.getXML(doExtendedParse);


	}
	else{
		// phone number already has a value so we don't need to populate it.
	}
}

function doExtendedParse(response){
	var answer = response.responseXML.documentElement.getAttribute('answer');
	var obj = JSON.parse(answer);
	if(obj.phone == ''){
		g_form.setValue("u_phone_number", "No Phone Number on Record");	
	}
	else{
		g_form.setValue("u_phone_number", obj.phone);
	}
}
