//DEFAULT
function highlights(){
	//Highlight affected fields
	(function () {
		var myVar2 = g_form.getControl('short_description');
		myVar2.style.width = '200px';
		// myVar2.style.backgroundColor = 'yellow';
		myVar2.style.color = 'purple';
		myVar2.style.fontWeight = 'bold';
		(function (node) {
			var level = 1;
			var step = function () {
				var hex = level.toString(16);
				node.style.backgroundColor = '#FFFF' + hex + hex;
				if (level < 15) {
					level += 1;
					setTimeout(step, 100);
				}
			};
			setTimeout(step, 100);
		}(myVar2));
	}());
}
