sys_script_email.script:
try {

	var changeNumber = event.parm1;
	var systemName = event.parm2;
	var message = '<p><img style="align: baseline;" src="/EmailBanner.pngx" alt="USCIS myIT Banner" align="baseline" border="" hspace="" vspace="" /></p>';

	var titleStyle = ' style="color: brown; font-size: 2em; font-weight: bold;"';
	var labelStyle = ' style="color: green; font-style: italic; border: 1px solid grey;"';
	var valueStyle = ' style="color: blue; border: 1px solid grey;"';

	message += '<p ' + titleStyle + '>Unable to locate ISSO: ' + systemName + '</p>';


	var chiefISSO = new GlideRecord('sys_properties');
	chiefISSO.addEncodedQuery('name=isso.top.level.approver');
	chiefISSO.query();
	if(chiefISSO.next()){
		var topUser = new GlideRecord('sys_user');
		topUser.addQuery('sys_id', chiefISSO.value);
		topUser.addEncodedQuery('active=true');
		topUser.query();
		if (topUser.next()){
			message += '<p> ' + 'The system <b>' + systemName + '</b> AA. <br> BB <b>CC</b> DDe. <br> EE. </p> <p>In order to ensure that this Change Request can move forward, the approval was sent to the current Risk Management Branch Chief: <b>' + topUser.name + ' </b>instead.</p>';
			email.addAddress("cc", topUser.email, topUser.name);
		}
	}




	template.print(message);

}
catch(e) {
	gs.log('Debug ISSO Missing Change email error: ' + e.message, 'ES: ISSO Missing Change');
}
