sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {

          // Add your code here
		var subject='';
	if(current.u_urgent_request==true){
		template.print('<p><span style="font-family: georgia, palatino; font-size: 12pt;">This Program Request has been marked URGENT and final adjudication is required within two weeks of the submitted date, '+ current.sys_created_on+'. Please review as early as possible.</span></p>');
	}else{
		template.print('<p><span style="font-family: georgia, palatino; font-size: 12pt;">You have 7 calendar days to review this request.</span></p>');

	}




})(current, template, email, email_action, event);
