sys_script_fix.script:
var gr = new GlideRecord('sc_req_item');
gr.addEncodedQuery('active=true^stateIN3,4,7');
gr.query();
var number = '';
while(gr.next()){
	number = '';
	number = gr.number;
	gr.active = false;
	gr.setWorkflow(false);
	gr.autoSysFields(false);
	gr.update();	
	gs.log(number + ' was set to inactive because the state is Closed.', 'FS: RITM - Set inactive if records closed');
}
