sys_script_fix.script:
var inc = new GlideRecord('incident');
inc.addEncodedQuery('state=8^ORstate=9^ORstate=7^active=true');
inc.query();
while(inc.next()){
	inc.work_notes = 'This record is being updated by an automated script to mark it as inactive due to the current state value.';
	inc.active=false;
	inc.update();	
}
