sys_script_fix.script:
var change = new GlideRecord('change_request');
change.addEncodedQuery('u_change_urgency!=1 - Critical^u_change_urgency!=2 - High^u_change_urgency!=3 - Medium^u_change_urgency!=4 - Low^u_change_urgencyANYTHING');
change.query();
while (change.next()){
	var message = '00 ' + change.number + '\n';
	if (change.u_change_urgency == '1-Critical'){
		change.u_change_urgency = '1 - Critical';
		message += '01 Urgency now set to 1 - Critial' + '\n';
	}
	if (change.u_change_urgency == '2-High'){
		change.u_change_urgency = '2 - High';
		message += '02 Urgency now set to 2 - High' + '\n';
	}
	if (change.u_change_urgency == '3-Medium'){
		change.u_change_urgency = '3 - Medium';
		message += '03 Urgency now set to 3 - Medium' + '\n';
	}
	if (change.u_change_urgency == '4-Low'){
		change.u_change_urgency = '4 - Low';
		message += '04 Urgency now set to 4 - Low' + '\n';
	}
	change.update();
	message += '05 Update completed.';
	gs.log(message, 'FS: Change Urgency Fix');
}
