sys_script_fix.script:
//var search_for = "Comment cannot be viewed by technicians until work begins on the Requested Item";
//"getStateMessage";
//

var search_for = "user_name";

searchForScript(search_for);
function searchForScript(script_fragment)
{
	var gr = new GlideRecord("sys_dictionary");
	gr.addQuery("internal_type.name", "CONTAINS", "Script");
	gr.orderBy("name");
	gr.orderBy("element");
	gr.query();
	var msg = "RESULTS FOR ("+script_fragment+")\n";
	var results = [];
	while(gr.next())
	{
		var gr_rec = new GlideRecord(gr.name);
		gr_rec.addQuery(gr.element, "CONTAINS", script_fragment);
		gr_rec.query();
		while(gr_rec.next())
			//exclude this fix script from the results
			if(!(gr.name == "sys_script_fix" && gr_rec.name == "[TOOL] script search"))
				results.push("RESULT >>> "+gr.name+"."+gr.element+" > "+gr_rec.getDisplayValue()+":"+gr_rec.sys_id);
	}

	if(results.length == 0)
		msg += "NO RESULTS FOUND";
	else
		msg += results.join("\n");

	gs.print(msg);
}
