sys_script_fix.script:
var recerts = new GlideRecord('u_recertification');
//update the encoded query below to update to whatever set of records need to be marked as rejected
recerts.addEncodedQuery('u_phase!=approved^ORu_phase=NULL^u_phase!=rejected^ORu_phase=NULL^u_system_name.u_sys_name=CLAIMS 4');
recerts.query();
while(recerts.next()){
	var workflow = new Workflow();
	workflow.cancel(recerts);
	recerts.u_phase = 'rejected';
	recerts.work_notes = 'This record has been marked rejected due to review and approval not being received.';
	recerts.update();
}
