sys_script_include.script:
var fsn_fileAttached_ajax = Class.create();
fsn_fileAttached_ajax.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	isAttached : function() {
		var gr_at = new GlideRecord("sys_attachment");

		var req_id = this.getParameter('sysparm_record_sysid');
		var tbl_name = this.getParameter('sysparm_table_name');
		var searchString = this.getParameter('sysparm_searchstring');
		var fileType = this.getParameter('sysparm_filetype');

		gr_at.addQuery("table_name",tbl_name);

		gr_at.addQuery("table_sys_id", req_id);

		gr_at.query();

		var count = 0;
		if (gr_at.next() ) {
			var fname = gr_at.file_name.upper();
			var ctype = gr_at.content_type.upper();
			while ((fname.indexOf(searchString) != -1 || searchString=="") && (ctype.indexOf(fileType) != -1 || fileType==""))
				{
				//return true;
					count++;
			}
			if(count<1){
			return false;
			}
				else{
					return true;

			}
		}
		else
			{

			return false;
		}
	},
    type: 'fsn_fileAttached_ajax'
});
