sys_script_include.script:
var STRY2572905 = Class.create();
STRY2572905.prototype = {
	initialize: function() {
	},

	/*
	exclude a specific role from having access to filters/breadcrumbs
	Users with the role "newrole" will not have access
	*/
	exclude : function incidentDisplayFilter() {
		if (gs.getUser().hasRoles() && !gs.hasRole("newrole")) {
			answer = true;
		} else {
			answer = false;
		}
		if (gs.hasRole("admin")) {
			answer = true;
		}
	},

	type: 'STRY2572905'
};
