sys_script_include.script:
var ScriptIncludeBuffer = Class.create();
ScriptIncludeBuffer.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
  getUserExtendedInfo: function () {

        var userID = this.getParameter('sysparm_userid');
        //gs.log('userID = ' + userID);
        var user = new GlideRecord('sys_user');
        user.addQuery('sys_id', userID);
        user.query();

        var info;
        if (user.next()) {

            info = '{"user_name":"' + user.user_name + '",' +
                '"full_name":"' + user.name + '",' +
                '"manager":"' + user.manager + '",' +
                '"title":"' + user.title + '",' +
                '"first":"' + user.first_name + '",' +
                '"middle":"' + user.middle_name + '",' +
                '"last":"' + user.last_name + '",' +
                '"location":"' + user.location + '",' +
                '"time_zone":"' + user.time_zone + '",' +
                '"phone":"' + user.phone + '",' +
                '"mobile":"' + user.mobile_phone + '",' +
                '"department":"' + user.department + '",' +
                '"company":"' + user.company + '",' +
                '"cube":"' + user.u_cube_number + '",' +
                '"email":"' + user.email + '",' +
                '"location_street":"' + user.location.street + '",' +
                '"location_city":"' + user.location.city + '",' +
                '"location_state":"' + user.location.state + '",' +
                '"location_zipcode":"' + user.location.zip + '",' +
                '"employee_type":"' + user.u_employee_type + '",' +
                '"location_country":"' + user.location.country + '",' +
                '"location_site_code":"' + user.location.u_site_code +
                '"}';
        }

        return info;
    },


    type: 'ScriptIncludeBuffer'
});
