function showActiveUsers(){
	var user = gs.getUser();  // get the current user object
	var answer = 'active=true';

	if (user.hasRole('admin')){  // check to see if the user has admin
		gs.log('User is an admin', 'SI: showActiveUsers');
		return answer;  // return all records if user is an admin		
	}

	if ((user.hasRole('vis_support')) || (user.hasRole('elis_support'))){
		gs.log('User has vis or elis role', 'SI: showActiveUsers');
		return answer;  // return all records if user has vis or elis role
	}

	// if user doesn't have any of the above roles then return users who are not external
	gs.log('User matches no role check', 'SI: showActiveUsers');
	answer = 'active=true^u_external_user!=true';
	return answer;

}
