sys_script_include.script:
var AjaxSessionExtender = Class.create();
AjaxSessionExtender.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	initialData: function() {
		//get data about the session timeout setup
		var data = this.newItem("data");

		var curr;
		// glide.ui.session_timeout
		data.setAttribute("session_timeout",  gs.getProperty("x_58872_needit.glide.ui.session_timeout") );

		// glide.ui.session_timeout_dialog_is_active
		curr = gs.getProperty("x_58872_needit.glide.ui.session_timeout_dialog_is_active");
		//gs.addErrorMessage("isActive ? "+ curr);
		data.setAttribute("dialog_enabled", curr);


		// glide.ui.session_timeout_dialog_offset
		curr = gs.getProperty("x_58872_needit.glide.ui.session_timeout_dialog_offset");
		//gs.addErrorMessage("offSet =  "+ curr);
		data.setAttribute("dialog_offset", curr);

		// glide.ui.session_timeout_dialog_text
		curr = gs.getProperty("x_58872_needit.glide.ui.session_timeout_dialog_text");
		//gs.addErrorMessage("TEXT :  =  "+ curr);
		data.setAttribute("dialog_text", curr);

		var user = this.newItem("user");
		curr = gs.getUser().hasRole("itil");
		//gs.addErrorMessage("SI: "+ curr);
		user.setAttribute("has_itil_role", curr);

		gs.addErrorMessage("Script Include: exit");
	},

	extendSession: function() {
		//do we even need to do anything?
		//no it's confirmed that we don't
	},
	type: 'AjaxSessionExtender'
});
