sys_script_include.script:
var CategoryItemsHelper = Class.create();
CategoryItemsHelper.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	getItemsByCategory: function(categoryID)
	{
		gs.log('Starting "getItemsByCategory" function.  categoryID = ' + categoryID, 'SI: CategoryItemsHelper');
		var groups = [];

		var gr = new GlideRecord('sc_cat_item_category');
		gr.addQuery('sc_category', categoryID);
		gr.query();
		while(gr.next())
			{
			if(gr.active)
				{
				var id = gr.sys_id.toString();
				groups.push(id);
			}
		}
		gs.log('groups = ' + groups, 'SI: CategoryItemsHelper');
		return groups;
	},


	getItemsInCategory: function(categoryID) {
		try
		{
			return this.getItemsByCategory(categoryID);
		}
		catch (e)
		{
			gs.log('ERROR: could not get list of items.');
			return [];
		}
	},


	getItemsQuery: function(categoryID) {
		return this.getQuery(this.getItemsInCategory(categoryID));
	},

	getItems: function() {
		gs.log('Starting "getItems" function.  new_category_parent = ' + current.variables.new_category_parent, 'SI: CategoryItemsHelper');
		var answer = '';
		var includes = current.variables.new_category_parent;
		var item = new GlideRecord('sc_cat_item_category');
		item.addQuery('sc_category',includes);
		item.query();
		while (item.next()) {
			gs.log('item.sc_cat_item.sys_id = ' + item.sc_cat_item.sys_id, 'SI: CategoryItemsHelper');
			if (answer.length > 0) {
				answer += (',' + item.sc_cat_item.sys_id);
			}
			else {
				answer = '' + item.sc_cat_item.sys_id;
			}
		}
		return 'sys_idIN' + answer;
	},
	type: 'CategoryItemsHelper'
});
