//InguanejsC2
var InguaneGetFieldName = Class.create();
InguaneGetFieldName.prototype = {
	initialize: function() {
	},
	//--------------------------------------------------------------
	//	printAllFieldsSysNames : function(tableName)
	//  Parameters:
	//       Table name
	//  Return:
	//        void-Displays all field aka columns names used by the system
	//---------------------------------------------------------------
	printAllFieldsSysNames : function(tableName){
		//------------------------------------------
		var semaphore = {x:1};
			this._P(semaphore);//AquireLock
			//use this method in the Business Rule
			var columns = new GlideRecord(tableName);
			columns.query();
			gs.addErrorMessage("$SERVER$: AquireLock " + " TOTAL ROWS = "+  columns.getRowCount());
			//-------------------------------------------
			columns.initialize();
			for (var column in columns){
				gs.addInfoMessage(column );
					//gs.addInfoMessage(column  +" : " + columns[column]);
			}
			//------------------------------------------
			columns.query();
			gs.addErrorMessage("$SERVER$: ReleaseLock " +" TOTAL ROWS = "+  columns.getRowCount());
			this._V(semaphore);//releaseLock
			//-------------------------------------------
		},
		//TODO implementation
		i_GetAllFieldsSysNames : function(tableName){
			//use this method in the Business Rule
			var newForm = new GlideRecord(tableName);
			//var currentFields = [];
			newForm.initialize();
			for (var f in newForm){
				//currentFields.push(f);
				gs.addInfoMessage(f);
			}
		},
		//TODO implementation
		i_GetAllFieldsDesplayrdNames : function(tableName){
			//use this method in the Business Rule
			var newForm = new GlideRecord(tableName);
			//var currentFields = [];
			newForm.initialize();
			for (var f in newForm){
				//currentFields.push(f);
				gs.addInfoMessage(f);
			}
		},
		//TODO implementation
		i_GetAllFieldsNamesJsonPairs : function(tableName){
			/*
			//use this method in the Business Rule
			var newForm = new GlideRecord(tableName);
			var fieldNamesJsonPais = [];
