			for (var f in newForm){
				fieldNamesJsonPais.push(f);
				gs.addInfoMessage(f);
			}
			return fieldNamesJsonPais;
			*/
					//------------------------------------------
		var semaphore = {x:1};
			this._P(semaphore);//AquireLock
			//use this method in the Business Rule
			var columns = new GlideRecord(tableName);
			columns.query();
			gs.addErrorMessage("$SERVER$: AquireLock" +" TOTAL ROWS = "+  columns.getRowCount());
			//-------------------------------------------
			columns.initialize();
			for (var column in columns){
				//gs.addInfoMessage(column );
			gs.addInfoMessage(column  +" : " + columns[column]);
			}
			//------------------------------------------
			columns.query();
			gs.addErrorMessage("$SERVER$: ReleaseLock" +" TOTAL ROWS = "+  columns.getRowCount());
			this._V(semaphore);//releaseLock
			//-------------------------------------------

		},
		_P : function (sem){
			while(sem.x <= 0);
				sem.x--;
		},

		_V : function(sem){
			sem.x++;
		},
		type: 'InguaneGetFieldName'
	};
