function getFSNWSponsor() {
	var message = '';
	var answer = '';
	var members = new GlideRecord('sys_user_grmember');
	members.addQuery('group', '4e957763db72f6003c80f81d0f9619df');
	members.query();
	message += 'getRowCount() = ' + members.getRowCount() + '\n';
	while(members.next()){
		if (answer.length > 0){
			message += 'answer.length is greater than 0 - adding member sys_id to the list' + '\n';
			answer+= (',' + members.user.sys_id);
		}
		else {
			message += 'answer.length is 0 - adding member sys_id to the list' + '\n';
			answer = '' + members.user.sys_id;
		}
	}
	message += answer + '\n';
	gs.log(message, 'SI: getFSNWSponsor');
	return 'sys_idIN' + answer;

}
