sysevent_in_email_action.script:
//	Note: current.opened_by is already set to the first UserID that matches the From: email address
(function runAction(
	/*GlideRecord u_inbound_emails */ current, 
	/*GlideRecord*/ event,
	/*GlideRecord*/sys_email,
	/*EmailWrapper*/ email, 
	/*ScopedEmailLogger*/ logger, 
	/*EmailClassifier*/ classifier
) {
	try {
		current.u_from_address = email.origemail;
		current.u_body = email.body_text;
		current.u_subject = email.subject;
		current.u_from = gs.getUserID();

		//gs.log('debug - email.importance' + email.importance);
		if (email.importance != undefined) {
			current.priority = email.importance;
		}
		current.insert();
	}
	catch(e) {
		gs.log('debug USCIS users Notify error: ' + e.message );
	}
})(current, event, sys_email, email, logger, classifier);
